#pragma config(Motor,  port1,           left,          tmotorVex393_HBridge, openLoop, reversed)
#pragma config(Motor,  port5,           openclose,     tmotorVex393_MC29, openLoop)
#pragma config(Motor,  port6,           touchclaw,     tmotorVex393_MC29, openLoop, reversed)
#pragma config(Motor,  port10,          right,         tmotorVex393_HBridge, openLoop)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//
#pragma platform(VEX2)
#pragma competitionControl(Competition)
#include "Vex_Competition_Includes.c"
void pre_auton()
{
	bStopTasksBetweenModes = true;
}
//do not mess with anthing above this!

//****************************************autonomus****************************************
task autonomous()
{
	motor[right] = 127;
	motor[left] = 127;
	wait1Msec(1500);
	motor[right] = 0;
	motor[left] = 127;

}
//****************************************usercontrol****************************************
task usercontrol()
{
	while (true)
	{
		//joystick controls for driving
		motor[right] = vexRT[Ch2];
		motor[left] = vexRT[Ch3];

		//Buttons to move arm
		if(vexRT[Btn5UXmtr2])
		{
			motor[touchclaw] = 120;
		}
		else if(vexRT[Btn5DXmtr2])
		{
			motor[touchclaw] = -80;
		}
		else
		{
			motor[touchclaw] = 0;
		}

		//Buttons to open / close claw
		if(vexRT[Btn6DXmtr2])
		{
			motor[openclose] = 120;
		}
		else if(vexRT[Btn6UXmtr2])
		{
			motor[openclose] = -120;
		}
		else
		{
			motor[openclose] = 0;
		}

		//Test Autonomous
		if(vexRT[Btn7L])
	{
			motor[right] = 127;
			motor[left] = 127;
			wait1Msec(1500);
			motor[right] = 0;
			motor[left] = 0;
			motor[right] = 127;
			wait1Msec(1000);
			motor[right] = 0;
		}
	}
}
